const express = require('express');
const bcrypt = require('bcrypt');
const db = require('../config/database');
const { authenticateToken } = require('../middleware/auth');

const router = express.Router();

// Profil bilgilerini getir
router.get('/profile', authenticateToken, async (req, res) => {
  try {
    const [users] = await db.execute(
      'SELECT id, full_name, email, phone, birth_date, role, created_at FROM users WHERE id = ?',
      [req.user.id]
    );
    
    if (users.length === 0) {
      return res.status(404).json({ error: 'Kullanıcı bulunamadı' });
    }
    
    res.json(users[0]);
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Profil bilgileri alınırken bir hata oluştu' });
  }
});

// Profil güncelle
router.put('/profile', authenticateToken, async (req, res) => {
  try {
    const { full_name, phone, birth_date, current_password, new_password } = req.body;
    const user_id = req.user.id;
    
    // Şifre değişikliği varsa
    if (current_password && new_password) {
      const [users] = await db.execute(
        'SELECT password_hash FROM users WHERE id = ?',
        [user_id]
      );
      
      const isValidPassword = await bcrypt.compare(current_password, users[0].password_hash);
      if (!isValidPassword) {
        return res.status(400).json({ error: 'Mevcut şifre hatalı' });
      }
      
      const hashedPassword = await bcrypt.hash(new_password, 10);
      await db.execute(
        'UPDATE users SET password_hash = ? WHERE id = ?',
        [hashedPassword, user_id]
      );
    }
    
    // Diğer bilgileri güncelle
    await db.execute(
      'UPDATE users SET full_name = ?, phone = ?, birth_date = ? WHERE id = ?',
      [full_name, phone, birth_date, user_id]
    );
    
    res.json({ message: 'Profil bilgileri güncellendi' });
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Profil güncellenirken bir hata oluştu' });
  }
});

module.exports = router;